import React, { useState } from 'react';

export const InterviewForm = () => {
  const [jobID, setJobID] = useState('');
  const [recruiterID, setRecruiterID] = useState('');
  const [candidateID, setCandidateID] = useState('');
  const [interviewDate, setInterviewDate] = useState('');
  const [location, setLocation] = useState('');
  const [notes, setNotes] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:8080/interview/create', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        jobID,
        recruiterID,
        candidateID,
        interviewDate,
        location,
        notes
      })
    })
      .then(res => res.json())
      .then(result => {
        if (result.success) {
          alert('Entretien planifié avec succès !');
        } else {
          alert(result.error || 'Erreur lors de la planification');
        }
      });
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto bg-white p-4 rounded shadow">
      <h2 className="text-xl font-bold mb-4">Planifier un entretien</h2>
      <input
        type="text"
        placeholder="Job ID"
        value={jobID}
        onChange={e => setJobID(e.target.value)}
        className="border p-2 mb-2 w-full"
        required
      />
      <input
        type="text"
        placeholder="Recruiter ID"
        value={recruiterID}
        onChange={e => setRecruiterID(e.target.value)}
        className="border p-2 mb-2 w-full"
        required
      />
      <input
        type="text"
        placeholder="Candidate ID"
        value={candidateID}
        onChange={e => setCandidateID(e.target.value)}
        className="border p-2 mb-2 w-full"
        required
      />
      <input
        type="datetime-local"
        value={interviewDate}
        onChange={e => setInterviewDate(e.target.value)}
        className="border p-2 mb-2 w-full"
        required
      />
      <input
        type="text"
        placeholder="Lieu"
        value={location}
        onChange={e => setLocation(e.target.value)}
        className="border p-2 mb-2 w-full"
      />
      <textarea
        placeholder="Notes"
        value={notes}
        onChange={e => setNotes(e.target.value)}
        className="border p-2 mb-2 w-full"
      />
      <button type="submit" className="bg-primary text-white px-4 py-2 rounded">Planifier</button>
    </form>
  );
};
