import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { LoginContext } from '../components/ContextProvider/Context';
import { MdEmail, MdMessage } from 'react-icons/md'; // Import des icônes

export const Footer = () => {
  const navigate = useNavigate();
  const { loginData } = useContext(LoginContext);
  const candidateID = loginData?._id || "667336c6ab92f179a717d0ec";

  const footerNav = [
    { label: "Jobs", path: "/all-jobs" },
    { label: "Login", path: "/login" },
    { label: "Signup", path: "/signup" },
    { label: "Post Job", path: "/post-job" },
    { label: "Interview", path: "/interview" },
    { label: "InterviewList", path: "/interview/Interviewdetails" },
    { label: "Evaluation-auto", path: "/evaluation-auto" }
    
  ];

  if (candidatId) {
  footerNav.push(
    { label: "Messagerie", path: `/messagerie/${candidatId}` },
    { label: "Notifier par Email", path: `/notifier/${candidatId}` }
  );
}

  return (
    <footer className="bg-white rounded-lg shadow m-4">
      <div className="w-full max-w-screen-xl mx-auto p-4 md:py-8">
        <div className="sm:flex sm:items-center sm:justify-between">
          <ul className="flex flex-wrap justify-center items-center mb-6 text-sm font-medium text-gray-500 sm:mb-0">
            {footerNav.map((menu, key) => (
              <li key={key}>
                <Link to={menu.path} className="hover:underline me-4 md:me-6">
                  {menu.label}
                </Link>
              </li>
            ))}
            {candidatId && (
              <li>
                <span
                  onClick={() => navigate(`/messagerie/${candidatId}`)}
                  className="hover:underline me-4 md:me-6 cursor-pointer text-blue-600 flex items-center"
                >
                  <MdMessage className="mr-1" /> Messagerie
                </span>
              </li>
            )}
            {candidatId && (
              <li>
                <span
                  onClick={() => navigate(`/notifier/${candidatId}`)}
                  className="hover:underline me-4 md:me-6 cursor-pointer text-green-600 flex items-center"
                >
                  <MdEmail className="mr-1" /> Notifier par Email
                </span>
              </li>
            )}
          </ul>
        </div>
        <hr className="my-6 border-gray-200 sm:mx-auto lg:my-8" />
        <span className="block text-sm text-gray-500 sm:text-center ">
          © {new Date().getFullYear()} <Link to="/" className="hover:underline">Innovation™</Link>. All Rights Reserved.
        </span>
      </div>
    </footer>
  );
};
