import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

// Composant d'évaluation
const EvaluationForm = ({ interviewId, evaluatorID }) => {
  const [note, setNote] = useState(0);
  const [commentaire, setCommentaire] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(`http://localhost:8080/interview/evaluate/${interviewId}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ note, commentaire, evaluatorID })
    })
      .then(res => res.json())
      .then(result => {
        if (result.success) {
          alert('Évaluation enregistrée !');
        } else {
          alert(result.error || 'Erreur lors de l\'évaluation');
        }
      });
  };

  return (
    <form onSubmit={handleSubmit} className="bg-gray-100 p-4 rounded my-4">
      <label>Note (1 à 5) :</label>
      <input
        type="number"
        min="1"
        max="5"
        value={note}
        onChange={e => setNote(e.target.value)}
        required
        className="border p-2 mx-2"
      />
      <label>Commentaire :</label>
      <textarea
        value={commentaire}
        onChange={e => setCommentaire(e.target.value)}
        className="border p-2 w-full my-2"
      />
      <button type="submit" className="bg-primary text-white px-4 py-2 rounded">Évaluer</button>
    </form>
  );
};

export const InterviewDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [interview, setInterview] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8080/interview/${id}`)
      .then(res => res.json())
      .then(data => setInterview(data));
  }, [id]);

  if (!interview) return <div>Chargement...</div>;

  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded shadow">
      <button
        className="mb-4 bg-gray-200 px-4 py-2 rounded hover:bg-gray-300"
        onClick={() => navigate('/interview/all')}
      >
        Liste des entretiens
      </button>
      <h1 className="text-2xl font-bold mb-4">Détails de l'entretien</h1>
      <p><b>Job ID :</b> {interview.jobID}</p>
      <p><b>Recruteur :</b> {interview.recruiterID}</p>
      <p><b>Candidat :</b> {interview.candidateID}</p>
      <p><b>Date :</b> {new Date(interview.interviewDate).toLocaleString()}</p>
      <p><b>Lieu :</b> {interview.location}</p>
      <p><b>Notes :</b> {interview.notes}</p>

      <h2 className="text-xl font-semibold mt-6 mb-2">Évaluer cet entretien</h2>
      {/* Remplace "ID_DU_RECRUTEUR" par l'ID du recruteur connecté */}
      <EvaluationForm interviewId={id} evaluatorID="ID_DU_RECRUTEUR" />

      {/* (Optionnel) Afficher les évaluations existantes */}
      {interview.evaluations && interview.evaluations.length > 0 && (
        <div className="mt-6">
          <h3 className="font-bold mb-2">Évaluations précédentes :</h3>
          <ul>
            {interview.evaluations.map((evalItem, idx) => (
              <li key={idx} className="mb-2">
                <b>Note :</b> {evalItem.note} / 5<br />
                <b>Commentaire :</b> {evalItem.commentaire}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};
